# wbc写的分词和清洗函数
def wash_as(str):
    wordlst = {}
    p1 = re.compile(r"\[.*?\]", re.S)
    list = re.findall(p1, str)
    for i in list:
        if i not in wordlst:
            wordlst[i] = 1
        else:
            wordlst[i] += 1
    for punct in wordlst.keys():
        str = str.replace(punct, ",")
    jieba.load_userdict('dict.txt')
    seglst = jieba.cut(str)
    for i in seglst:
        if i not in wordlst:
            wordlst[i] = 1
        else:
            wordlst[i] += 1
    for punct in """！？｡＂＃＄％＆＇（）＊＋，－／：；＜＝＞＠［＼］＾＿｀｛｜｝～｟｠｢｣､、〃《》「」『』【】〔〕〖〗〘〙〚〛〜〝〞〟〰〾〿–—‘'‛“”„‟…‧﹏.!#$%&\'()*+,-./:;<=>?@[\\]^_`{|}~"
    """:
        if punct in wordlst:
            wordlst.pop(punct)
    poplst = []
    for char in wordlst.keys():
        if len(char) == 1:
            poplst.append(char)
    for char in poplst:
        wordlst.pop(char)
    for char in str:
        if ord(char) < 129686 and ord(char) > 127743:
            if char not in wordlst:
                wordlst[char] = 1
            else:
                wordlst[char] += 1
    return wordlst


print(wash_as(Str))

